namespace Message.Infrastructure.Persistence;

public class MessageDbContextSeed(ILogger<MessageDbContextSeed> logger, MessageDbContext context)
{
    private readonly ILogger<MessageDbContextSeed> _logger = logger;
    private readonly MessageDbContext _context = context;

    public async Task InitialiseAsync()
    {
        try
        {
            await _context.Database.MigrateAsync();
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "An error occurred while initializing the database.");
            throw;
        }
    }

    public async Task SeedAsync()
    {
        try
        {
            await TrySeedAsync();
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "An error occurred while seeding the database.");
            throw;
        }
    }

    private async Task TrySeedAsync()
    {
        // Seed authors
        if (!_context.Authors.AsQueryable().Any())
        {
            List<AuthorEntity> authors =
            [
                    new("John", "Doe", ""),
                    new("Alice", "Smith", "")
                ];

            await _context.Authors.AddRangeAsync(authors);
            _logger.LogInformation("Seeded Authors: {@Authors}", authors);
        }

        // Seed messages
        if (!_context.Messages.AsQueryable().Any())
        {
            string messagesJson = @"[
                    {
                        ""Author"": {
                            ""FirstName"": ""John"",
                            ""LastName"": ""Doe"",
                            ""Id"": ""1"",
                            ""ImageUrl"": """"
                        },
                        ""CreatedAt"": 1655648403000,
                        ""Height"": 1280,
                        ""Id"": ""1"",
                        ""Name"": ""Chat 1"",
                        ""Size"": 585000,
                        ""Status"": ""seen"",
                        ""Text"": ""Hello!"",
                        ""Type"": ""text"",
                        ""Uri"": ""https://example.com/chat1.jpg"",
                        ""Width"": 1920,
                        ""MimeType"": ""image/jpeg""
                    },
                    {
                        ""Author"": {
                            ""FirstName"": ""Alice"",
                            ""LastName"": ""Smith"",
                            ""Id"": ""2"",
                            ""ImageUrl"": """"
                        },
                        ""CreatedAt"": 1655648403000,
                        ""Height"": 720,
                        ""Id"": ""2"",
                        ""Name"": ""Chat 2"",
                        ""Size"": 300000,
                        ""Status"": ""sent"",
                        ""Text"": ""How are you?"",
                        ""Type"": ""text"",
                        ""Uri"": ""https://example.com/chat2.jpg"",
                        ""Width"": 1280,
                        ""MimeType"": ""image/jpeg""
                    }
                ]";

            List<MessageEntity>? messages = JsonConvert.DeserializeObject<List<MessageEntity>>(messagesJson);
            if (messages != null)
            {
                await _context.Messages.AddRangeAsync(messages);
                _logger.LogInformation("Seeded Messages: {@Messages}", messages);
            }
            await _context.SaveChangesAsync();
        }
    }
}